// Footer.js

import React, { useState } from 'react';
import { SocialIcon } from 'react-social-icons';
import './Footer.css';

const Footer = () => {
  const [iconColors, setIconColors] = useState({
    twitter: 'grey',
    instagram: 'grey',
    youtube: 'grey',
    facebook: 'grey',
    github: 'grey',
  });

  const handleMouseEnter = (icon) => {
    setIconColors((prevColors) => ({
      ...prevColors,
      [icon]: getHoverColor(icon),
    }));
  };

  const handleMouseLeave = (icon) => {
    setIconColors((prevColors) => ({
      ...prevColors,
      [icon]: 'grey',
    }));
  };

  const getHoverColor = (icon) => {
    switch (icon) {
      case 'twitter':
        return '#17A1F2';
      case 'instagram':
        return '#E1306C';
      case 'youtube':
        return '#FF4500';
      case 'facebook':
        return '#0D6EDB';
      case 'github':
        return '#0D0D0D';
      default:
        return 'grey';
    }
  };

  return (
    <footer className="footer">
      <div className="left-section">
        <p>&copy; 2024 Your Name</p>
      </div>
      <div className="right-section">
        <SocialIcon
          style={{ height: 40, width: 40, cursor: 'pointer', marginRight: '5px' }}
          className="custom-class"
          bgColor={iconColors.twitter}
          url="https://twitter.com/sajjdemon"
          target="_blank"
          onMouseEnter={() => handleMouseEnter('twitter')}
          onMouseLeave={() => handleMouseLeave('twitter')}
        />
        <SocialIcon
          style={{ height: 40, width: 40, cursor: 'pointer', marginRight: '5px' }}
          className="custom-class"
          bgColor={iconColors.instagram}
          url="https://instagram.com/sajjdemon"
          target="_blank"
          onMouseEnter={() => handleMouseEnter('instagram')}
          onMouseLeave={() => handleMouseLeave('instagram')}
        />
        <SocialIcon
          style={{ height: 40, width: 40, cursor: 'pointer', marginRight: '5px' }}
          className="custom-class"
          bgColor={iconColors.youtube}
          url="https://youtube.com/c/sajjdemon"
          target="_blank"
          onMouseEnter={() => handleMouseEnter('youtube')}
          onMouseLeave={() => handleMouseLeave('youtube')}
        />
        <SocialIcon
          style={{ height: 40, width: 40, cursor: 'pointer', marginRight: '5px' }}
          className="custom-class"
          bgColor={iconColors.facebook}
          url="https://facebook.com/sajjaademon"
          target="_blank"
          onMouseEnter={() => handleMouseEnter('facebook')}
          onMouseLeave={() => handleMouseLeave('facebook')}
        />
        <SocialIcon
          style={{ height: 40, width: 40, cursor: 'pointer' }}
          className="custom-class"
          bgColor={iconColors.github}
          url="https://github.com/ignite312"
          target="_blank"
          onMouseEnter={() => handleMouseEnter('github')}
          onMouseLeave={() => handleMouseLeave('github')}
        />
      </div>
    </footer>
  );
};

export default Footer;
