import React, { useState, useEffect } from 'react';
import './App.css';
import './Styles.css';

const CodeforcesInfo = () => {
  const [codeforcesData, setCodeforcesData] = useState(null);
  const [okCount, setOkCount] = useState(0);

  useEffect(() => {
    // Fetch Codeforces data and update the state
    // Replace the following with actual data fetching logic

    const fetchData = async () => {
      // Example URL for recent submissions
      const handle = 'tourist';
      const apiUrl = `https://codeforces.com/api/user.status?handle=${handle}&from=1&count=100000000`;

      try {
        const response = await fetch(apiUrl);
        const data = await response.json();

        // Check if the response status is OK
        if (data.status === 'OK') {
          // Update state with the recent submissions data
          setCodeforcesData(data.result);

          // Count the number of submissions with verdict "OK"
          const okSubmissions = data.result.filter(submission => submission.verdict === 'OK');
          setOkCount(okSubmissions.length);
        } else {
          console.error('Codeforces API request failed with status:', data.status);
        }
      } catch (error) {
        console.error('Error fetching Codeforces data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="card">
      <h2>Codeforces Information</h2>
      {codeforcesData && (
        <div className="card-content">
          <h3>Recent Submissions:</h3>
          <p>Total submissions with Verdict "OK": {okCount}</p>
          <ul>
            {codeforcesData.map(submission => (
              <li key={submission.id}>
                Problem: {submission.problem.name}, Verdict: {submission.verdict}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CodeforcesInfo;
