import React from 'react';

const Octagon = ({ imageName, imageUrl, description }) => {
  const styles = {
    card: {
      display: 'flex',
      maxWidth: '400px', // Adjust the maximum width as needed
      margin: '20px',
      border: '1px solid #ccc',
      borderRadius: '8px',
      overflow: 'hidden',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    leftSide: {
      flex: '1',
      padding: '20px',
    },
    rightSide: {
      flex: '2',
      padding: '20px',
      backgroundColor: '#f8f8f8', // Optional background color for the right side
    },
    image: {
      width: '100%',
      height: 'auto',
      objectFit: 'cover',
      borderRadius: '8px',
    },
    imageName: {
      marginTop: '10px',
      fontSize: '16px',
    },
  };

  return (
    <div style={styles.card}>
      <div style={styles.leftSide}>
        <img src={imageUrl} alt={imageName} style={styles.image} />
        <p style={styles.imageName}>{imageName}</p>
      </div>
      <div style={styles.rightSide}>
        <p>{description}</p>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <div>
      <Octagon
        imageName="Octagon Image 1"
        imageUrl="https://example.com/octagon-image-1.jpg"
        description="Lorem ipsum dolor sit amet, consectetur adipiscing elit."
      />

      <Octagon
        imageName="Octagon Image 2"
        imageUrl="https://example.com/octagon-image-2.jpg"
        description="Another Octagon with a different image and description."
      />
    </div>
  );
};

export default App;
