// GitHubInfo.js

import React, { useState, useEffect } from 'react';
import './App.css';
import './Styles.css';


const GitHubInfo = () => {
  const [githubData, setGithubData] = useState(null);

  useEffect(() => {
    // Fetch GitHub data and update the state
    // Replace the following with actual data fetching logic

    const fetchData = async () => {
      // Example URL
      const apiUrl = 'https://api.github.com/users/ignite312';

      try {
        const response = await fetch(apiUrl);
        const data = await response.json();
        setGithubData(data);
      } catch (error) {
        console.error('Error fetching GitHub data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="card">
      <h3>GitHub</h3>
      <h2>Handle : ignite312</h2>
      {githubData && (
        <div className="card-content">
          <p>Contribution Count: {githubData.contributions}</p>
          <p>Repositories: {githubData.public_repos}</p>
          <p>Followers: {githubData.followers}</p>
        </div>
      )}
    </div>
  );
};

export default GitHubInfo;
