import React, { useState, useEffect } from 'react';
import './Styles.css';

const AtCoderInfo = () => {
  const [atCoderData, setAtCoderData] = useState(null);
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    // Fetch AtCoder data and update the state
    const fetchData = async () => {
      const handle = 'ignite312';

      // Example URL for user information
      const userInfoUrl = `https://kenkoooo.com/atcoder/atcoder-api/v3/user_info?user=${handle}`;

      try {

        // Fetch AtCoder user information data
        const userInfoResponse = await fetch(userInfoUrl);
        const userInfoData = await userInfoResponse.json();

        // Check if the API request was successful
        if (userInfoData) {
          // Update state with AtCoder user details
          setUserDetails(userInfoData);
        } else {
          console.error('AtCoder API request failed.');
        }
      } catch (error) {
        console.error('Error fetching AtCoder data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="card">
      <h3>AtCoder </h3>
      {userDetails && (
        <div className="card-content">
          <h2>Handle: {userDetails.username}</h2>
          <p>Rating: {userDetails.rating}</p>
          {/* Add more details from the AtCoder API as needed */}
        </div>
      )}
    </div>
  );
};

export default AtCoderInfo;
