import React, { useState, useEffect } from 'react';
import './App.css';
import './Styles.css';
import githubLogo from './logo/github.png';
import axios from 'axios';
import cheerio from 'cheerio';

const GitHubInfo = () => {
  const [githubData, setGithubData] = useState(null);
  const [contributionCount, setContributionCount] = useState(null);

  const fetchGitHubData = async () => {
    const userApiUrl = 'https://api.github.com/users/ignite312';
    const eventsApiUrl = `https://github.com/users/ignite312/contributions?to=2016-12-31`;

    try {
      // Fetch user data
      const userResponse = await axios.get(userApiUrl);
      if (!userResponse.data) {
        throw new Error(`GitHub API request failed: ${userResponse.statusText}`);
      }

      const userData = userResponse.data;

      // Fetch SVG calendar content
      const eventsResponse = await axios.get(eventsApiUrl);
      if (!eventsResponse.data) {
        throw new Error(`GitHub API request failed: ${eventsResponse.statusText}`);
      }

      // Parse the SVG content using cheerio
      const $ = cheerio.load(eventsResponse.data);

      // Extract and sum the data-count attribute from all rect elements
      const contributions = $('rect[data-count]')
        .map((index, element) => parseInt($(element).attr('data-count'), 10))
        .get();

      // Calculate the total contribution count
      const totalContributions = contributions.reduce((acc, count) => acc + count, 0);

      // Set the states
      setContributionCount(totalContributions);
      setGithubData({
        login: userData.login,
        public_repos: userData.public_repos,
        followers: userData.followers,
        following: userData.following,
      });
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
    }
  };

  useEffect(() => {
    fetchGitHubData();
  }, []);

  return (
    <div className="card">
      <h3>GitHub</h3>
      <img src={githubLogo} alt="GitHub Logo" className="logo" />
      {githubData && (
        <div className="card-content">
          <h2>Handle: {githubData.login}</h2>
          <div style={{ color: 'green' }}>
            <span style={{ color: 'black' }}>Contribution Count: </span>
            <strong>{contributionCount !== null ? `+${contributionCount}` : 'Loading...'}</strong>
          </div>
          <p>Public Repositories: {githubData.public_repos}</p>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
            <p>Followers: {githubData.followers}</p>
            <p>Following: {githubData.following}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default GitHubInfo;
