import React, { useState, useEffect } from 'react';
import './App.css';
import './Styles.css';

const GitHubInfo = () => {
  const [githubData, setGithubData] = useState(null);

  const fetchGitHubData = async () => {
    const apiUrl = 'https://api.github.com/graphql';
    const token = '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'; // Replace with your GitHub Personal Access Token

    const query = `
      query {
        viewer {
          contributionsCollection {
            contributionCalendar {
              totalContributions
            }
          }
          login
          repositoriesContributedTo {
            totalCount
          }
          repositories(first: 100, isFork: false, privacy: PUBLIC) {
            totalCount
          }
          followers {
            totalCount
          }
        }
      }
    `;

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ query }),
      });

      const data = await response.json();

      if (data.errors) {
        throw new Error(`GitHub API request failed: ${data.errors[0].message}`);
      }

      setGithubData(data.data.viewer);
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
    }
  };

  const fetchPublicRepos = async () => {
    const apiUrl = 'https://api.github.com/users/ignite312';
    try {
      const response = await fetch(apiUrl);
      const data = await response.json();
      setGithubData((prevData) => ({
        ...prevData,
        repositories: { totalCount: data.public_repos },
      }));
    } catch (error) {
      console.error('Error fetching public repositories count:', error);
    }
  };

  useEffect(() => {
    fetchGitHubData();
    fetchPublicRepos();
  }, []);

  return (
    <div className="card">
      <h3>GitHub</h3>
      {githubData && githubData.contributionsCollection && githubData.contributionsCollection.contributionCalendar && (
        <div className="card-content">
          <h2>Handle: {githubData.login}</h2>
          <p>Contribution Count: {githubData.contributionsCollection.contributionCalendar.totalContributions}</p>
          {githubData.repositories && githubData.repositories.totalCount && (
            <p>Public Repositories: {githubData.repositories.totalCount}</p>
          )}
          <p>Followers: {githubData.followers.totalCount}</p>
        </div>
      )}
    </div>
  );
};

export default GitHubInfo;
