import React, { useState, useEffect } from 'react';
import './App.css';
import './Styles.css';
import githubLogo from './logo/github.png';

const GitHubInfo = () => {
  const [githubData, setGithubData] = useState(null);
  const [events, setEvents] = useState(null);

  const getEvents = async (username) => {
    const events = [];
    let page = 1;

    do {
      const url = `https://api.github.com/users/${username}/events?page=${page}`;
      var body = await fetch(url).then((res) => res.json());
      page++;
      events.push(...body);
    } while (!body.length);

    return events;
  };

  const fetchGitHubData = async () => {
    const userApiUrl = 'https://api.github.com/users/ignite312';

    try {
      const userResponse = await fetch(userApiUrl);

      if (!userResponse.ok) {
        throw new Error(`GitHub API request failed: ${userResponse.statusText}`);
      }

      const userData = await userResponse.json();

      // Fetch GitHub events
      const userEvents = await getEvents('ignite312');

      setGithubData({
        login: userData.login,
        public_repos: userData.public_repos,
        followers: userData.followers,
        following: userData.following,
      });

      setEvents(userEvents);
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
    }
  };

  useEffect(() => {
    fetchGitHubData();
  }, []);

  return (
    <div className="card">
      <h3>GitHub</h3>
      <img src={githubLogo} alt="GitHub Logo" className="logo" />
      {githubData && (
        <div className="card-content">
          <h2>Handle: {githubData.login}</h2>
          <div style={{ color: 'green' }}>
            <span style={{ color: 'black' }}>Contribution Count: </span>
            <strong>{'++'}</strong>
          </div>
          <p>Public Repositories: {githubData.public_repos}</p>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
            <p>Followers: {githubData.followers}</p>
            <p>Following: {githubData.following}</p>
          </div>
          {events && (
            <div>
              <h3>GitHub Events</h3>
              <p>Total Events: {events.length}</p>
              <p>Pull Requests: {events.filter((event) => event.type === 'PullRequestEvent').length}</p>
              <p>Forks: {events.filter((event) => event.type === 'ForkEvent').length}</p>
              <p>Issues: {events.filter((event) => event.type === 'IssuesEvent').length}</p>
              <p>Reviews: {events.filter((event) => event.type === 'PullRequestReviewEvent').length}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default GitHubInfo;
