// Footer.js

import React from 'react';
import { SocialIcon } from 'react-social-icons';

const Footer = () => {
  const iconStyle = {
    cursor: 'pointer',
    backgroundColor: 'grey',
    padding: '6px', // Adjust padding as needed
    borderRadius: '50%', // Make the icons circular
    transition: 'background-color 0.3s ease, color 0.3s ease', // Add smooth transition for background-color and color
    fontSize: '10px', // Adjust font size
  };

  return (
    <footer className="footer">
      <div className="left-section">
        <p>&copy; 2024 Your Name</p>
      </div>
      <div className="right-section">
        {/* Apply inline styles for each social icon */}
        <SocialIcon style={iconStyle} url="https://linkedin.com/in/sajjademon" target="_blank" />
        <SocialIcon style={iconStyle} url="https://twitter.com/sajjdemon" target="_blank" />
        <SocialIcon style={iconStyle} url="https://instagram.com/sajjdemon" target="_blank" />
        <SocialIcon style={iconStyle} url="https://youtube.com/c/sajjdemon" target="_blank" />
        <SocialIcon style={iconStyle} url="https://facebook.com/sajjaademon" target="_blank" />
        <SocialIcon style={iconStyle} url="https://github.com/ignite312" target="_blank" />
      </div>
    </footer>
  );
};

export default Footer;
